mapper.select:
/* kr.co.windrider.MyLife.getMyLife */ SELECT UUID AS uuid ,IMG_URL AS imgUrl ,TITLE AS title ,CONTENTS AS contents ,DELETE_YN AS deleteYn ,UPDATE_USER AS updateUser ,UPDATE_DATE AS updateDate ,CREATE_USER AS createUser ,CREATE_DATE AS createDate FROM MY_LIFE WHERE 1 = 1
AND UUID = #{myLifeVo.uuid}
ORDER BY CREATE_DATE DESC

mapper.insert:
	/* kr.co.windrider.MyLife.saveMyLife */
        INSERT INTO MY_LIFE ( UUID    ,IMG_URL     ,TITLE       ,CONTENTS    ,DELETE_YN   ,UPDATE_USER ,UPDATE_DATE ,CREATE_USER ,CREATE_DATE )
        VALUES(              uuid()   ,#{myLifeVo.imgUrl}   ,#{myLifeVo.title}     ,#{myLifeVo.contents}  ,'N'         ,NULL        ,NULL        ,'admin'     ,now()       )
